import React, { Component } from 'react';
import { Row, Col, Form, Icon, Input, Button, Checkbox, Tabs,Alert } from 'antd';
import "./forget.css";
import { Steps } from 'antd';
import Head from '../../components/user/Head';
import Footer from '../../components/user/Footer';

const Step = Steps.Step;
export default class forget extends Component {

    render() {
        return (
            <div id="background" style={{ backgroundImage: 'url("https://gw.alipayobjects.com/zos/rmsportal/TVYTbAXWheQpRcWDaDMu.svg")' }}>
                <Head />
                <Row style={{ marginTop: "8rem" }}>
                    <Col xs={24} lg={{ span: 10, offset: 7 }}>
                        <Steps progressDot size="small" current={0}>
                            <Step title="验证手机号" />
                            <Step title="设置密码" />
                            <Step title="结果页" />
                        </Steps>
                    </Col>
                    <Col xs={{ span: 22, offset: 1 }} lg={{ span: 8, offset: 8 }} style={{ marginTop: "2rem" }}>
                        <Verify />
                    </Col>
                </Row>
                <Footer />
            </div>
        )
    }
}
// 第一步校验手机号
class Verify extends Component {

    constructor(props){
        super(props);
        this.state={
            code:""
        }
    }

    verifySubmit=()=>{ //校验提交

    }
    updateCode=(e)=>{ //验证码输入触发事件
        console.log(e.target);
    }
    render() {
        const {code} = this.state;
        console.log(code);
        return (
            <div className="lis">
                <div className="li_label">
                    <div className="label">手机号</div>
                    <div className="input">15129430537</div>
                </div>
                <div className="li_label">
                    <div className="label">验证码</div>
                    <div className="input"> <Input name="code" onChange={this.updateCode.bind(this)} value={code} prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} type="text" placeholder="验证码" maxLength={4} /><Button style={{ marginLeft: "1rem" }}>获取验证码</Button></div>
                </div>

                <Button type="primary" block style={{marginTop:"1rem",height:"45px"}}>提交</Button>
            </div>
        )
    }
}
