import apiManager from './apiManager';
import axios from 'axios';
import { Toast } from 'antd'

// let domain = "http://159x64197e.iask.in";
let domain = "http://192.168.0.153:8082";
let apis = apiManager.api;
//设置请求允许携带cookie信息
axios.defaults.withCredentials = true;
// 拦截请求
axios.interceptors.request.use(function (config) {
    Toast.loading('加载中', 0);
    return config
});

// 拦截相应
axios.interceptors.response.use(function (config) {
    Toast.hide();
    return config
});
const apiRequest = {
    all: (apiName, data, success, errorMsg) => {
        let api = apis[apiName];
        return new Promise((reslove, reject) => {
            axios(api.method==="GET"?domain + api.url+(data!==null?data:''):{
                method: api.method,
                url: domain + api.url,
                data: data,
                timeout: 10000
            }).then((response) => {
                let data = response.data;
                if (data.code > 1) {
                    Toast.offline(data.msg, 1);
                } else if (success !== null) {
                    Toast.success(success, 1);
                } else {
                    reslove(response.data);
                    return response.data;
                }
            }).catch((error) => {
                reject(error);
                Toast.offline(errorMsg, 1);
            });
        })
    }
}
export default apiRequest;
