import React, { Component } from 'react';
import { Tabs, Select, Table, Row, Col, Button, Input, Modal, Form, Badge, List, Icon, message } from 'antd';
import './rbac.less';
import province from '../../mock/geographic/province.json';
import city from '../../mock/geographic/city.json';
import district from '../../mock/geographic/district.json';
import apiRequest from '../../servers/api/apiRequest';
const Search = Input.Search;
const TabPane = Tabs.TabPane;
const Option = Select.Option;


const formItemLayout2 = {
  labelCol: { span: 4 },
  wrapperCol: { span: 12 },
};
const { TextArea } = Input;
export default class rbac extends Component {
  state = {
    tabPosition: 'left',
    accountData: [],
    pagination: {
      total: 0,
      pageSize: 10
    },
    current: 0,
    visible: false,
    tabData: [],
    roles: []
  }

  constructor(props) {
    super(props);
  }

  componentDidMount() {
    var { current } = this.state;
    this.queryPage(current, this.state.pagination.pageSize);
    this.selectRoleAll();

  }

  updateState = (id, state) => {  //更改账号状态
    let promise = apiRequest.all("updateState", id + "/" + state, null, "服务器繁忙请稍后重试！");
    var thic = this;
    promise.then(function (value) {
      if (value.code === 1) {
        thic.queryPage(thic.state.current, thic.state.pagination.pageSize);
      }
    })
  }
  queryPage = (current, size, name) => { //分页查询数据
    let promise = apiRequest.all("adminQueryPage", current + "/" + size + (name != undefined ? "?name=" + name : ""), null, "服务器繁忙请稍后重试！");
    var thic = this;
    promise.then(function (value) {
      if (value.code === 1) {
        let pagination = thic.state.pagination;
        pagination.total = value.data.count;
        pagination.onChange = thic.paging.bind(this)
        thic.setState({
          tabData: value.data.data,
          pagination: thic.state.pagination
        });
      }
    })
  }

  selectRoleAll = () => {
    let promise = apiRequest.all("queryAllRole", null, null, "服务器繁忙请稍后重试！");
    var thic = this;
    promise.then(function (value) {
      if (value.code === 1) {
        thic.setState({
          roleDatas: value.data
        })
      }
    })
  }

  showModal2 = (roleObj) => { //角色添加框
    this.setState({
      visible2: true,
      roleObj:roleObj
    });
  }


  paging = (page, pageSize) => {
    this.queryPage((page - 1) * 10, pageSize);
  }

  handleCancel = (e) => {
    this.setState({
      visible: false,
    });
  }

  handleCancel2 = (e) => {
    this.setState({
      visible2: false,
    });
  }
  handleSelectChange = (value) => {
    this.props.form.setFieldsValue({
      note: `Hi, ${value === 'male' ? 'man' : 'lady'}!`,
    });
  }
  showModal = () => {
    this.setState({
      visible: true
    });
  }
  removeRole=(id,index)=>{ //删除角色
    let promise = apiRequest.all("removeRole", id, "删除成功", "服务器繁忙请稍后重试！");
    var thic = this;
    promise.then(function (value) {
      if (value.code === 1) {
        const {roleDatas} = thic.state;
        roleDatas.splice(index,1);
        thic.setState({
          roleDatas: roleDatas
        })
      }
    })
  }


  render() {
    const { tabData, pagination, roleDatas,roleObj} = this.state;
    const columns = [
      {
        title: '编号',
        dataIndex: 'id',
        key: 'id',
        render: (text, record, index) => {
          return index + 1;
        }
      }, {
        title: '账号',
        dataIndex: 'account',
        key: 'account',
      }, {
        title: '角色',
        dataIndex: 'role',
        key: 'role',
      }, {
        title: '创建时间',
        dataIndex: 'createTime',
        key: 'createTime',
        render: (text, record, index) => {
          return new Date(text).toLocaleString();
        }
      }, {
        title: '状态',
        dataIndex: 'state',
        key: 'state',
        render: (text, record, index) => {
          switch (text) {
            case 0:
              return (
                <span style={{ color: '#52c41a' }}><Badge status="success" />正常</span>
              )
              break;
            case 1:
              return (
                <span style={{ color: 'red' }}><Badge status="error" />冻结</span>
              )
              break;
            default:
              return "未知"
          }
        }
      }, {
        title: '最近登录时间',
        dataIndex: 'lastTime',
        key: 'lastTime',
        render: (text, record, index) => {
          if (text != null && text.length > 0) {
            return new Date(text).toLocaleString();
          }
        }
      }, {
        title: '操作',
        dataIndex: 'id',
        key: 'caozuo',
        render: (text, record, index) => {
          switch (record.state) {
            case 1:
              return (
                <Button onClick={this.updateState.bind(this, record.id, 0)}>解冻</Button>
              )
              break;
            case 0:
              return (
                <Button onClick={this.updateState.bind(this, record.id, 1)}>冻结</Button>
              )
              break;
            default:
              return "未知"
          }


        }
      }];
    const data = [
      {
        title: 'Ant Design Title 1',
      },
      {
        title: 'Ant Design Title 2',
      },
      {
        title: 'Ant Design Title 3',
      },
      {
        title: 'Ant Design Title 4',
      },
    ];
    return (
      <div style={{ background: '#fff', height: '100%' }}>
        {/* 添加、更新用户 */}
        <Modal
          visible={this.state.visible}
          onOk={this.addUser}
          onCancel={this.handleCancel}
          closable={false}
          destroyOnClose={true}
          footer={null}
        >
          <AddUserFrom handleCancel={this.handleCancel.bind(this)} queryPage={this.queryPage.bind(this)} size={pagination.pageSize}></AddUserFrom>
        </Modal>
        <Tabs tabPosition={this.state.tabPosition} tabBarGutter={10} animated style={{ height: '75vh', width: '100%' }}>
          <TabPane tab="账号管理" key="1">
            <div className="tabPane">
              <Row gutter={32} style={{ padding: '1rem  0 1rem 0' }}>
                <Col span={12} style={{ paddingLeft: '0' }}>  <Search
                  placeholder="账号、编号查询"
                  onSearch={(value) => {
                    this.queryPage(0, this.state.pagination.pageSize, value)
                    console.log(value);
                  }}
                  style={{ width: 200 }}
                /></Col>
                <Col span={11} style={{ textAlign: 'right', paddingRight: '0' }}><Button type="primary" onClick={this.showModal}>添加</Button></Col>
              </Row>
              <Row gutter={32}>
                <Table
                  dataSource={tabData}
                  columns={columns}
                  pagination={this.state.pagination}
                  loading={this.state.loading}
                  style={{ marginRight: '24px' }}
                />
              </Row>
            </div>
          </TabPane>
          <TabPane tab="菜单管理" key="2">

          </TabPane>
          <TabPane tab="功能权限" key="3">
            <Row>
              <Col span={5} style={{ height: '75vh', overflowY: 'auto', overflowX: 'hidden' }}>
                {/* 添加、更新角色 */}
                <Modal
                  visible={this.state.visible2}
                  onOk={this.addUser}
                  onCancel={this.handleCancel2}
                  closable={false}
                  destroyOnClose={true}
                  footer={null}
                >
                  <AddRoleFrom handleCancel={this.handleCancel2.bind(this)} selectRoleAll={this.selectRoleAll} roleObj={roleObj}></AddRoleFrom>
                </Modal>
                <List
                  itemLayout="horizontal"
                  dataSource={roleDatas}
                  bordered={true}
                  renderItem={(item,index) => (
                    <List.Item>
                      <List.Item.Meta style={{ width:'60px',overflow:'hidden' }}
                        title={<a href="https://ant.design" style={{overflow:'hidden'}}>{item.name}</a>}
                        description={item.desc}
                      />
                      {/* 操作 */}
                      <div className="operation">
                        <Icon type="edit"  style={{ marginRight: '1rem',marginLeft:'0.5rem' }} onClick={this.showModal2.bind(this,item)} />
                        <Icon type="delete"  onClick={this.removeRole.bind(this,item.id,index)}/>
                      </div>
                    </List.Item>

                  )}>
                  <Button type="dashed" block style={{ position: 'absolute', bottom: '0', height: '3rem', bottom: '-2' }} onClick={this.showModal2.bind(this)}><Icon type="plus" />添加角色</Button>
                </List>
              </Col>
              <Col span={18} offset={1} style={{ border: '1px solid #d9d9d9', height: '75vh' }}>

              </Col>
            </Row>
          </TabPane>
        </Tabs>
      </div >
    )
  }
}
class AddRole extends Component {

  constructor(props) {
    super(props)
    this.state = {
      loading: false
    }
  }

  addRole = (e) => { //添加 || 更新 角色
    this.setState({
      loading: true
    });
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        let id = this.props.roleObj.id;
        if(id!=undefined){
          values.id=id;
        }
        let promise = apiRequest.all("addRole", values, "更新成功", "服务器繁忙请稍后重试！");
        var thic = this;
        promise.then(function (value) {
          if (value.code === 1) {
            thic.setState({
              loading: false
            });
            thic.props.handleCancel();
            thic.props.selectRoleAll();
          }

        })
      }
    })
  }



  render() {
    const formItemLayout = {
      labelCol: { span: 4 },
      wrapperCol: { span: 10 },
    };
    const { loading } = this.state;
    const { getFieldDecorator } = this.props.form;
    const roleObj = this.props.roleObj;
    console.log(roleObj);
    return (
      <Form >
        <Form.Item
          label="角色名称"
          required
          {...formItemLayout}
          style={{ marginBottom: '0' }}
        >
          {getFieldDecorator('name', {
            rules: [{ required: true, message: '请填写角色名称' }],
            initialValue:(roleObj.name===undefined?'':roleObj.name)
          })(
            <Input name="name"  placeholder="请输入角色名称" type="text" />
          )}
        </Form.Item>
        <Form.Item
          label="描述"
          required={false}
          {...formItemLayout}
          style={{ marginBottom: '0' }}
        >
          {getFieldDecorator('desc', {
            rules: [{ required: false }],
            initialValue:(roleObj.desc===undefined?'':roleObj.desc)
          })(
            <Input placeholder="请输入角色描述"  type="text" maxLength={50} />
          )}

        </Form.Item>
        <div style={{ width: '100%', textAlign: 'center', marginTop: '1rem' }}>
          <Button key="back" onClick={this.props.handleCancel}>取消</Button>
          <Button key="submit" type="primary" loading={loading} onClick={this.addRole} style={{ marginLeft: '10px' }}>
            提交
            </Button>
        </div>
      </Form>
    );
  }
}
class AddUser extends Component {
  constructor(props) {
    super(props)
    this.state = {
      roles: [],
      loading: false
    }
  }
  getRole = () => {
    var roles = [{
      label: "代理商",
      value: 1
    }, {
      label: "企业用户",
      value: 2
    }];
    this.setState({
      roles: roles
    })
  }

  componentDidMount() {
    this.getRole();
  }
  addUser = (e) => { //添加用户
    e.preventDefault();
    this.setState({
      loading: true
    });
    this.props.form.validateFields((err, values) => {
      if (!err) {
        var data = {
          account: values.account,
          roleid: values.roleId
        };
        let thic = this;
        let promise = apiRequest.all("saveAdmin", data, "添加成功", null);
        promise.then(function (value) {
          if (value.code === 1) {
            thic.props.queryPage(0, thic.props.size);
            thic.props.handleCancel();
          }
          thic.setState({
            loading: false
          });
        })
      }
    });
  }

  render() {
    const formItemLayout = {
      labelCol: { span: 4 },
      wrapperCol: { span: 10 },
    };
    const { roles, loading } = this.state;
    const { getFieldDecorator } = this.props.form;
    return (
      <Form >
        <Form.Item
          label="账号"
          required
          {...formItemLayout}
          style={{ marginBottom: '0' }}
        >
          {getFieldDecorator('account', {
            rules: [{ required: true, message: '请填写您的账号' }],
          })(
            <Input placeholder="请输入账号" type="text" />
          )}
        </Form.Item>
        <Form.Item
          label="角色"
          required
          {...formItemLayout}
          style={{ marginBottom: '0' }}
        >
          {getFieldDecorator('roleId', {
            rules: [{ required: true, message: '请选择角色' }],
          })(
            <Select style={{ width: 120 }}>
              {
                roles.map((item, index) => {
                  return (
                    <Option value={item.value} key={index}>{item.label}</Option>
                  )
                })
              }
            </Select>
          )}
        </Form.Item>
        <div style={{ width: '100%', textAlign: 'center', marginTop: '1rem' }}>
          <Button key="back" onClick={this.props.handleCancel}>取消</Button>
          <Button key="submit" type="primary" loading={loading} onClick={this.addUser} style={{ marginLeft: '10px' }}>
            提交
            </Button>
        </div>
      </Form>
    )
  }
}
const AddUserFrom = Form.create()(AddUser);
const AddRoleFrom = Form.create()(AddRole);
