import React, { Component } from 'react';
import { Tabs, Select, Table, Row, Col, Button, Input, Modal, Form, Cascader } from 'antd';
import './rbac.less';
import province from '../../mock/geographic/province.json';
import city from '../../mock/geographic/city.json';
import district from '../../mock/geographic/district.json';
import apiRequest from '../../servers/api/apiRequest';
const Search = Input.Search;
const TabPane = Tabs.TabPane;
const Option = Select.Option;
const columns = [
  {
    title: '编号',
    dataIndex: 'id',
    render: name => `${name.first} ${name.last}`
  }, {
    title: '账号',
    dataIndex: '',
    render: name => `${name.first} ${name.last}`
  }, {
    title: '代理地区',
    dataIndex: '',
    render: name => `${name.province} ${name.city} ${name.district}`
  }, {
    title: '角色',
    dataIndex: '',
  }, {
    title: '创建时间',
    dataIndex: '',
  }, {
    title: '状态',
    dataIndex: '',
  }, {
    title: '最近登录时间',
    dataIndex: '',
  }, {
    title: '操作',
    dataIndex: '',
  }];
const formItemLayout = {
  labelCol: { span: 4 },
  wrapperCol: { span: 8 },
};

class rbac extends Component {
  state = {
    tabPosition: 'left',
    accountData: [],
    pagination: {
      total: 50,
      pageSizeOptions: [10, 20, 30, 40, 50]
    },
    visible: false
  }

  constructor(props) {
    super(props);

  }

  componentDidMount() {
    this.getProvince();
  }

  getProvince = () => { //获取省市
    var result = []

    for (let i = 0; i < province.length; i++) {
      var obj = province[i];
      var citys = city[obj.id];
      var p_c = [];
      if (citys !== undefined) {
        for (let a = 0; a < citys.length; a++) {
          var obj_c = citys[a];
          if (obj_c.province === obj.name) {
            const pro = {
              value: obj_c.id,
              label: obj_c.name
            }
            p_c.push(pro);
          }
        }
      }
      const pro = {
        value: obj.id,
        label: obj.name,
        children: p_c
      }
      result.push(pro);
    }
    this.setState({
      options: result
    })
  }
  addUser = (e) => { //添加用户
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        var data = {
          account: values.account,
          province: values.p_c[0],
          city: values.p_c[1],
          roleid: values.roleid[0],
          name: values.name,
          phone: values.phone
        };
        let promise = apiRequest.all("saveAdmin", data, "添加成功", null);
        promise.then(function (value) {
            console.log(value);
          if (value.code === 1) {
            // thic.state.seachLis = value.data.topWords[0]
            // thic.setState(thic.state);
          }
        })
      }
    });
  }

  showModal = () => {
    this.getRole();
    this.setState({
      visible: true,
      loading: false
    });
  }

  getRole = () => {
    var roles = [{
      label: "代理商",
      value: 1
    }, {
      label: "企业用户",
      value: 2
    }];
    this.setState({
      roles: roles
    })
  }



  handleCancel = (e) => {
    this.setState({
      visible: false,
    });
  }
  handleSelectChange = (value) => {
    this.props.form.setFieldsValue({
      note: `Hi, ${value === 'male' ? 'man' : 'lady'}!`,
    });
  }
  render() {
    const { tabs, options, roles, loading } = this.state;
    const { getFieldDecorator } = this.props.form;
    return (
      <div style={{ background: '#fff', height: '100%' }}>
        {/* 添加、更新用户 */}
        <Modal
          visible={this.state.visible}
          onOk={this.addUser}
          onCancel={this.handleCancel}
          closable={false}
          destroyOnClose={true}
          footer={[
            <Button key="back" onClick={this.handleCancel}>取消</Button>,
            <Button key="submit" type="primary" loading={loading} onClick={this.addUser}>
              提交
            </Button>,
          ]}
        >
          <Form >
            <Form.Item
              label="账号"
              required
              {...formItemLayout}
            >
              {getFieldDecorator('account', {
                rules: [{ required: true, message: '请填写您的账号' }],
              })(
                <Input placeholder="请输入账号" type="text" />
              )}
            </Form.Item>
            <Form.Item
              label="代理地区"
              required
              {...formItemLayout}
            >
              {getFieldDecorator('p_c', {
                rules: [{ required: true, message: '请选择您所代理城市' }],
              })(
                <Cascader options={options} placeholder="请选择代理地区" />
              )}
            </Form.Item>
            <Form.Item
              label="角色"
              required
              {...formItemLayout}
            >
              {getFieldDecorator('roleid', {
                rules: [{ required: true, message: '请选择角色' }],
              })(
                <Cascader options={roles} placeholder="请选择角色" />
              )}
            </Form.Item>
            <Form.Item
              label="姓名"
              required
              {...formItemLayout}
            >
              {getFieldDecorator('name', {
                rules: [{ required: true, message: '您的姓名不能为空呦' }],
              })(
                <Input placeholder="请输入联系人姓名" type="text" />
              )}
            </Form.Item>
            <Form.Item
              label="手机号"
              required
              {...formItemLayout}
            >
              {getFieldDecorator('phone', {
                rules: [{ required: true, message: '请输入您的11位手机号码' }],
              })(
                <Input placeholder="请输入联系方式" type="tel" />
              )}
            </Form.Item>
          </Form>
        </Modal>
        <Tabs tabPosition={this.state.tabPosition} tabBarGutter={10} animated style={{ height: '75vh', width: '100%' }}>
          <TabPane tab="账号管理" key="1">
            <div className="tabPane">
              <Row gutter={32} style={{ padding: '1rem  0 1rem 0' }}>
                <Col span={12} style={{ paddingLeft: '0' }}>  <Search
                  placeholder="账号、角色、编号查询"
                  onSearch={value => console.log(value)}
                  style={{ width: 200 }}
                /></Col>
                <Col span={11} style={{ textAlign: 'right', paddingRight: '0' }}><Button type="primary" onClick={this.showModal}>添加</Button></Col>
              </Row>
              <Row gutter={32}>
                <Table
                  columns={columns}
                  rowKey={record => record.login.uuid}
                  pagination={this.state.pagination}
                  loading={this.state.loading}
                  onChange={this.handleTableChange}
                  style={{ marginRight: '24px' }}
                />
              </Row>
            </div>
          </TabPane>
          <TabPane tab="功能权限" key="3">

          </TabPane>
          <TabPane tab="数据权限" key="2">

          </TabPane>
        </Tabs>
      </div >
    )
  }
}
export default Form.create()(rbac);
