import React, { Component } from 'react';
import { Row, Col, Form, Icon, Input, Button, Checkbox, Tabs } from 'antd';
import "./forget.css";
import { Steps } from 'antd';
import Head from '../../components/user/Head';
import Footer from '../../components/user/Footer';

const Step = Steps.Step;
export default  class forget extends Component {
     
    render() {
        return (
            <div id="background" style={{ backgroundImage: 'url("https://gw.alipayobjects.com/zos/rmsportal/TVYTbAXWheQpRcWDaDMu.svg")' }}>
                <Head />
                <Row style={{ marginTop: "8rem" }}>
                    <Col xs={24} lg={{ span: 10, offset: 7 }}>
                        <Steps progressDot size="small" current={0}>
                            <Step title="验证手机号" />
                            <Step title="设置密码" />
                            <Step title="结果页" />
                        </Steps>
                    </Col>
                    <Col xs={24} lg={{ span: 8, offset: 8 }}>
                        <VerifyForm />
                    </Col>
                </Row>
                <Footer />
            </div>
        )
    }
}
// 第一步校验手机号
class verify extends Component{

    render(){
        const { getFieldDecorator } = this.props.form;
        return(
            <Form onSubmit={this.handleSubmit} className="login-form">
            <Form.Item label="手机号">
               <span>15129430537</span>
            </Form.Item>
            <Form.Item>
                {getFieldDecorator('password', {
                    rules: [{ required: true, message: 'Please input your Password!' }],
                })(
                    <Input prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} type="password" placeholder="Password" />
                )}
            </Form.Item>
            <Form.Item>
                <Button type="primary" htmlType="submit" className="login-form-button">
                   提交
                </Button>
            </Form.Item>
        </Form>
        )
    }
}
const VerifyForm = Form.create()(verify);
