import React, { Component } from 'react';
import { Tabs, Select, Table, Row, Col, Button, Input, Modal, Form, Cascader } from 'antd';
import './rbac.less';
import province from '../../mock/geographic/province.json';
import city from '../../mock/geographic/city.json';
import district from '../../mock/geographic/district.json';
import apiRequest from '../../servers/api/apiRequest';
const Search = Input.Search;
const TabPane = Tabs.TabPane;
const Option = Select.Option;
const columns = [
  {
    title: '编号',
    dataIndex: 'id',
    render: name => `${name.first} ${name.last}`
  }, {
    title: '账号',
    dataIndex: '',
    render: name => `${name.first} ${name.last}`
  }, {
    title: '角色',
    dataIndex: '',
  }, {
    title: '创建时间',
    dataIndex: '',
  }, {
    title: '状态',
    dataIndex: '',
  }, {
    title: '最近登录时间',
    dataIndex: '',
  }, {
    title: '操作',
    dataIndex: '',
  }];
const formItemLayout = {
  labelCol: { span: 4 },
  wrapperCol: { span: 8 },
};

class rbac extends Component {
  state = {
    tabPosition: 'left',
    accountData: [],
    pagination: {
      total: 50,
      pageSizeOptions: [10, 20, 30, 40, 50]
    },
    size:10,
    current:0,
    visible: false,
    tabData:[]
  }

  constructor(props) {
    super(props);

  }

  queryPage=(current,size)=>{
    let promise = apiRequest.all("adminQueryPage", "/"+current+"/"+pageSize, null, "服务器繁忙请稍后重试！");
    var thic =this;
    promise.then(function (value) {
      if (value.code === 1) {
        let total = value.count;
        let pageSize=Math.round(total/size);
        let pageSizeOptions = [];
        for(let i=0;i<pageSize;i++){
          pageSizeOptions.push(size*i);
        }
        let pagination={
          total: total,
          pageSizeOptions:pageSizeOptions
        }
        thic.setState({
          tabData: value.data,
          pagination: pagination
        });
      }
    })
  }

  addUser = (e) => { //添加用户
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        var data = {
          account: values.account,
          province: values.p_c[0],
          city: values.p_c[1],
          roleid: values.roleid[0],
          name: values.name,
          phone: values.phone
        };
        let thic = this;
        let promise = apiRequest.all("saveAdmin", data, "添加成功", null);
        promise.then(function (value) {
          if (value.code === 1) {
            thic.setState({
              loading: false,
              visible: false
            });
          }
        })
      }
    });
    this.setState({
      loading: true
    });
  }

  showModal = () => {
    this.getRole();
    this.setState({
      visible: true,
      loading: false
    });
  }

  getRole = () => {
    var roles = [{
      label: "代理商",
      value: 1
    }, {
      label: "企业用户",
      value: 2
    }];
    this.setState({
      roles: roles
    })
  }



  handleCancel = (e) => {
    this.setState({
      visible: false,
    });
  }
  handleSelectChange = (value) => {
    this.props.form.setFieldsValue({
      note: `Hi, ${value === 'male' ? 'man' : 'lady'}!`,
    });
  }
  render() {
    const { tabs, options, roles, loading } = this.state;
    const { getFieldDecorator } = this.props.form;
    return (
      <div style={{ background: '#fff', height: '100%' }}>
        {/* 添加、更新用户 */}
        <Modal
          visible={this.state.visible}
          onOk={this.addUser}
          onCancel={this.handleCancel}
          closable={false}
          destroyOnClose={true}
          footer={[
            <Button key="back" onClick={this.handleCancel}>取消</Button>,
            <Button key="submit" type="primary" loading={loading} onClick={this.addUser}>
              提交
            </Button>,
          ]}
        >
          <Form >
            <Form.Item
              label="账号"
              required
              {...formItemLayout}
            >
              {getFieldDecorator('account', {
                rules: [{ required: true, message: '请填写您的账号' }],
              })(
                <Input placeholder="请输入账号" type="text" />
              )}
            </Form.Item>
          </Form>
        </Modal>
        <Tabs tabPosition={this.state.tabPosition} tabBarGutter={10} animated style={{ height: '75vh', width: '100%' }}>
          <TabPane tab="账号管理" key="1">
            <div className="tabPane">
              <Row gutter={32} style={{ padding: '1rem  0 1rem 0' }}>
                <Col span={12} style={{ paddingLeft: '0' }}>  <Search
                  placeholder="账号、编号查询"
                  onSearch={value => console.log(value)}
                  style={{ width: 200 }}
                /></Col>
                <Col span={11} style={{ textAlign: 'right', paddingRight: '0' }}><Button type="primary" onClick={this.showModal}>添加</Button></Col>
              </Row>
              <Row gutter={32}>
                <Table
                  columns={columns}
                  rowKey={record => record.login.uuid}
                  pagination={this.state.pagination}
                  loading={this.state.loading}
                  onChange={this.handleTableChange}
                  style={{ marginRight: '24px' }}
                />
              </Row>
            </div>
          </TabPane>
          <TabPane tab="功能权限" key="3">

          </TabPane>
          <TabPane tab="数据权限" key="2">

          </TabPane>
        </Tabs>
      </div >
    )
  }
}
export default Form.create()(rbac);
