import React from 'react';
import { Row, Col, Form, Icon, Input, Button, Checkbox } from 'antd';
import './login.css';
import LOGO from '../../common/images/LOGO.png'
import propTypes from 'prop-types';
const FormItem = Form.Item;
export default class Login extends React.Component {



    componentDidMount() {
    }

    render() {
        return (

            //栅格布局
            <div id="background" style={{ backgroundImage: 'url("https://gw.alipayobjects.com/zos/rmsportal/TVYTbAXWheQpRcWDaDMu.svg")' }}>
                <Row>
                    {/* LOGO、名称 */}
                    <Col id="login_head" span={8} offset={8}>
                        <div className="head_logo" style={{backgroundImage: 'url("'+LOGO+'")',height:"60px",width:'60px'}}></div>
                    </Col>
                    <Col id="login_from_col" span={8} offset={8}>
                        <WrappedNormalLoginForm />
                    </Col>
                </Row>
            </div>
        )
    }

}
// 登陆表单组件
class NormalLoginForm extends React.Component {

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }
        });
    }

    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: { span: 4 },
            wrapperCol: { span: 14 },
        };
        return (
            <Form onSubmit={this.handleSubmit} className="login-form">
                <FormItem  {...formItemLayout}>
                    {getFieldDecorator('userName', {
                        rules: [{ required: true, message: 'Please input your username!' }],
                    })(
                        <Input prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />} placeholder="用户名" />
                    )}
                </FormItem>
                <FormItem  {...formItemLayout}>
                    {getFieldDecorator('password', {
                        rules: [{ required: true, message: 'Please input your Password!' }],
                    })(
                        <Input prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />} type="password" placeholder="密码" />
                    )}
                </FormItem>
                <FormItem  {...formItemLayout}>
                    {getFieldDecorator('remember', {
                        valuePropName: 'checked',
                        initialValue: true,
                    })(
                        <Checkbox>自动登录</Checkbox>
                    )}
                    <Button type="primary" htmlType="submit" className="login-form-button">
                        登录
            </Button>
                </FormItem>
            </Form>
        );
    }
}

const WrappedNormalLoginForm = Form.create()(NormalLoginForm);
